import React, { Component } from "react";
import CheckItem from "./checkitems";
import Addcheckitem from "./addcheckitem";

const token =
  "52615ebb3fb8336a474fd1ab9ec8ae053f5321433e1cbfefefb33a1779816ba9";
const url = "https://api.trello.com";
const key = "23fe0646c0d1253eb430f7e02db925a0";

class CheckList extends Component {
  state = {
    //checkItems: this.props.checklist.checkItems,
    addcheckitem: false,
    input: "",
  };

  deleteCheckItem = (checkitemid) => {
    let url = `https://api.trello.com/1/checklists/${this.props.checklist.id}/checkItems/${checkitemid}?key=${key}&token=${token}`;
    fetch(url, {
      method: "DELETE",
    });
    this.setState({
      checkItems: this.state.checkItems.filter((obj) => obj.id !== checkitemid),
    });
  };

  showCheckItemCard = () => {
    this.setState({
      addcheckitem: true,
    });
  };

  cancelCheckItemCard = (e) => {
    e.preventDefault();
    this.setState({
      addcheckitem: false,
    });
  };

  handleInput = (e, input) => {
    e.preventDefault();
    let url = `https://api.trello.com/1/checklists/${this.props.checklist.id}/checkItems?name=${input}&key=${key}&token=${token}`;
    fetch(url, {
      method: "POST",
    })
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          checkItems: result,
        });
      });
  };

  //   handleChange = (e) => {
  //     this.setState({
  //       input: e.target.value,
  //     });
  //   };

  render() {
    return (
      <div className="checkitem">
        <div className="d-flex justify-content-between align-items-center">
          {this.props.checklist.name}
          <button
            className="btn btn-light"
            onClick={(e) => this.props.deleteChecklist(this.props.checklist.id)}
          >
            Remove
          </button>
        </div>
        <div className="display-checkitems">
          {this.state.checkItems.map((checkitems) => {
            return (
              <CheckItem
                key={checkitems.id}
                checkitems={checkitems}
                checklist={this.props.checklist}
                deleteCheckItem={this.deleteCheckItem}
              />
            );
          })}
        </div>
        {this.state.addcheckitem ? (
          <Addcheckitem
            onAdd={this.handleInput}
            onDelete={this.cancelCheckItemCard}
          />
        ) : (
          <div
            className="btn btn secondary  add-card"
            onClick={this.showCheckItemCard}
          >
            Add item
          </div>
        )}
      </div>
    );
  }
}
export default CheckList;
