import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { Modal, Button } from "react-bootstrap";
import Input from "./addcard";
const token =
  "52615ebb3fb8336a474fd1ab9ec8ae053f5321433e1cbfefefb33a1779816ba9";
const url = "https://api.trello.com";
const key = "23fe0646c0d1253eb430f7e02db925a0";

class ChecklistModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      checklist: [],
      cardId: props.cardId,
      addChecklist: false,
      show: true,
      input: "",
    };
  }

  componentDidMount() {
    let url = `https://api.trello.com/1/cards/${this.state.cardId}/checklists?key=${key}&token=${token}`;
    fetch(url)
      .then((res) => res.json())
      .then((result) => {
        this.setStae({
          checklist: [
            ...this.state.checklist,
            { name: result.name, checklistid: [result.id, result.checkItems] },
          ],
        });
      });
  }

  render() {
    return (
      <Modal show={this.state.show} onHide={this.handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{this.props.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* {this.state.addChecklist ? (
            <Checklist
              onAdd={this.createChecklist}
              onDelete={this.handleRemove}
            />
          ) : (
            <div className="add-card">
              {/* onClick={this.handleRemove} }
              <button type="btn btn-light mb-2">Add checklist</button>
            </div>
          )*/}

          {this.state.checklist.map((items) => {
            console.log(items);
            return (
              <div className="checklist" key={items.checklistid[0]}>
                {items.name}

                {/* <button
                  onClick={() => this.deletechecklist(items.checklistid[0])}
                  className="btn btn-light"
                >
                  X
                </button> */}
              </div>
            );
          })}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary">
            {/* onClick ={this.handleRemove} */}
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default ChecklistModal;
