import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Input = (props) => {
  return (
    <form onAdd={props.handleInput}>
      <input
        type="text"
        className="form-control "
        placeholder="enter card name"
        onChange={props.inputText}
        value={props.value}
      />

      <button className="btn btn-success mr-4 mt-2">Add</button>

      <button className="btn btn-light" onClick={props.onDelete}>
        X
      </button>
    </form>
  );
};
export default Input;
