import React, { Component } from "react";
//import Card from "./Card";
const token =
  "52615ebb3fb8336a474fd1ab9ec8ae053f5321433e1cbfefefb33a1779816ba9";
const url = "https://api.trello.com";
const key = "23fe0646c0d1253eb430f7e02db925a0";
class List extends Component {
  state = {
    // addCard: false,
    cards: [],
  };
  handleCard = () => {
    <form className="form-control">
      <input type="text" placeholder="enter cardname"></input>
    </form>;
  };
  componentDidMount() {
    let url = `https://api.trello.com/1/lists/5e8838bc95f9447a48ade567/cards?key=${key}&token=${token}`;
    fetch(url)
      .then((res) => res.json())
      .then((result) => {
        // console.log(result);
        //this.setState({ cards: result });
        let data = result;
        for (let i = 0; i < data.length; i++) {
          let cardname = data[i].name;
          let cardid = data[i].id;
          this.setState(() => ({
            cards: [
              ...this.state.cards,
              {
                name: cardname,
                id: cardid,
              },
            ],
          }));
        }
      });
  }
  render() {
    const cards = this.state.cards;
    console.log(cards);
    return (
      <div className="list">
        <h2>Doing </h2>
        <button
          onClick={() => this.handleCard()}
          className="btn btn-light add-card"
        >
          Add Another Card
        </button>
      </div>
    );
  }
}

export default List;
