import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { Modal, Button } from "react-bootstrap";
import Input from "./addcard";
import CheckList from "./checklist";
const token =
  "52615ebb3fb8336a474fd1ab9ec8ae053f5321433e1cbfefefb33a1779816ba9";
const url = "https://api.trello.com";
const key = "23fe0646c0d1253eb430f7e02db925a0";

class ChecklistModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      show: this.props.show,
      checklist: [],
      //cardId: this.props.cards.id,
      // addChecklist: false,
      removeModal: this.props.removeModal,
      input: "",
    };
  }

  handleInput = (e, input) => {
    e.preventDefault();
    let url = `https://api.trello.com/1/checklists?idCard=${this.props.modal.id}&name=${input}&key=${key}&token=${token}`;
    fetch(url, {
      method: "POST",
    })
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
        let cardname = result.name;
        let cardid = result.id;
        this.setState({
          checklist: [
            ...this.state.checklist,
            {
              name: cardname,
              id: cardid,
              key: cardid,
            },
          ],
        });
      });
  };

  handleChange = (e) => {
    this.setState({ input: e.target.value });
  };

  deleteChecklist = (checklistid) => {
    let url = `https://api.trello.com/1/checklists/${checklistid}?key=${key}&token=${token}`;
    fetch(url, {
      method: "DELETE",
    });
    this.setState({
      checklist: this.state.checklist.filter((obj) => obj.id !== checklistid),
    });
  };

  componentDidMount() {
    let url = `https://api.trello.com/1/cards/${this.props.modal.id}/checklists?key=${key}&token=${token}`;
    fetch(url, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((result) => {
        let data = result;
        for (let i = 0; i < data.length; i++) {
          let cardname = data[i].name;
          let cardid = data[i].id;
          this.setState(() => ({
            checklist: [
              ...this.state.checklist,
              {
                name: cardname,
                id: cardid,
                key: cardid,
              },
            ],
          }));
        }
      });
  }

  render() {
    const { input } = this.state;
    return (
      <Modal show={this.props.show} onHide={this.props.removeModal}>
        <Modal.Header closeButton>
          <Modal.Title>{this.props.modal.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div classname="body">
            <form>
              <h5>Add checklist</h5>
              <input
                type="text"
                className="form-control mb-2"
                onChange={this.handleChange}
                value={input}
              />
              <button
                type="submit"
                className="btn btn-success mt-2"
                onClick={(e) => this.handleInput(e, input)}
              >
                Add
              </button>
            </form>
          </div>

          {this.state.checklist.map((items) => {
            return (
              <CheckList
                key={items.id}
                checklistdata={items}
                deleteChecklist={this.deleteChecklist}
              />
            );
          })}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={this.props.removeModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default ChecklistModal;
